import React, { useState, useEffect } from 'react';
import { AppBar, Toolbar, Typography, Button, Box, Dialog, DialogTitle, DialogContent, DialogActions, List, 
  Card, CardContent } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import LocalOfferIcon from '@mui/icons-material/LocalOffer';
import { admin } from '../../services/api'; // Assuming you have an API service

const Navbar = () => {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const role = localStorage.getItem('role');



  const [openNewCoupons, setOpenNewCoupons] = useState(false);
  const [openAssignedCoupons, setOpenAssignedCoupons] = useState(false);
  const [newCoupons, setNewCoupons] = useState([]);
  const [assignedCoupons, setAssignedCoupons] = useState([]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    navigate('/');
  };


  const fetchNewCoupons = async () => {
    try {
      const response = await admin.getNewCoupons();
      setNewCoupons(response.data);
      setOpenNewCoupons(true);
    } catch (err) {
      console.error('Failed to fetch new coupons');
    }
  };

  const fetchAssignedCoupons = async () => {
    try {
      const response = await admin.getAssignedCoupons();
      setAssignedCoupons(response.data);
      setOpenAssignedCoupons(true);
    } catch (err) {
      console.error('Failed to fetch assigned coupons');
    }
  };

  return (
    <>
      <AppBar position="static" sx={{ marginBottom: 4 }}>
        <Toolbar>
          <LocalOfferIcon sx={{ mr: 2 }} />
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            CouponHub
          </Typography>
          <Box>
            {role === 'ROLE_ADMIN' && (
              <>
                <Button color="inherit" onClick={fetchNewCoupons}>
                  New Coupons
                </Button>
                <Button color="inherit" onClick={fetchAssignedCoupons}>
                  Assigned Coupons
                </Button>
              </>
            )}
            {token ? (
              <Button color="inherit" onClick={handleLogout}>
                Logout
              </Button>
            ) : (
              <>
                <Button color="inherit" onClick={() => navigate('/login')}>
                  Login
                </Button>
                <Button color="inherit" onClick={() => navigate('/register')}>
                  Register
                </Button>
              </>
            )}
          </Box>
        </Toolbar>
      </AppBar>

      <Dialog open={openNewCoupons} onClose={() => setOpenNewCoupons(false)} fullWidth maxWidth="sm">
        <DialogTitle sx={{ backgroundColor: '#009879', color: '#fff', textAlign: 'center' }}>New Coupons</DialogTitle>
        <DialogContent sx={{ backgroundColor: '#f3f4f6' }}>
          {newCoupons.length > 0 ? (
            <List>
              {newCoupons.map((coupon) => (
                <Card key={coupon.id} sx={{ mb: 2, boxShadow: 3, p: 2 }}>
                  <CardContent>
                    <Typography variant="h6" color="primary">
                      {coupon.code}
                    </Typography>
                    <Typography variant="body2" color="textSecondary">
                      Description: <strong>{coupon.description}</strong>
                    </Typography>
                    <Typography variant="body2" color="textSecondary">
                      Discount: <strong>{coupon.discountAmount}%</strong>
                    </Typography>

                    {/* User Dropdown Selection */}
                    <FormControl fullWidth sx={{ mt: 2 }}>
                      <InputLabel>Select User</InputLabel>
                      <Select
                        value={selectedUsers[coupon.id] || ""}
                        onChange={(e) =>
                          setSelectedUsers((prev) => ({ ...prev, [coupon.id]: e.target.value }))
                        }
                        displayEmpty
                      >
                        {users.map((user) => (
                          <MenuItem key={user.id} value={user.id}>
                            {user.name}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>

                    {/* Assign Button */}
                    <Button
                      variant="contained"
                      color="primary"
                      sx={{ mt: 2, width: '100%' }}
                      disabled={!selectedUsers[coupon.id]}
                      onClick={() => handleAssignCoupon(coupon.id, selectedUsers[coupon.id])}
                    >
                      Assign to User
                    </Button>
                  </CardContent>
                </Card>
              ))}
            </List>
          ) : (
            <Typography textAlign="center" color="textSecondary">
              No new coupons available.
            </Typography>
          )}
        </DialogContent>
        <DialogActions sx={{ justifyContent: 'center', backgroundColor: '#f3f4f6' }}>
          <Button onClick={() => setOpenNewCoupons(false)} variant="contained" color="error">
            Close
          </Button>
        </DialogActions>
      </Dialog>

      {/* Assigned Coupons Modal */}
      <Dialog open={openAssignedCoupons} onClose={() => setOpenAssignedCoupons(false)} fullWidth maxWidth="sm">
        <DialogTitle sx={{ backgroundColor: '#009879', color: '#fff', textAlign: 'center' }}>Assigned Coupons</DialogTitle>
        <DialogContent sx={{ backgroundColor: '#f3f4f6' }}>
          {assignedCoupons.length > 0 ? (
            <List>
              {assignedCoupons.map((coupon) => (
                <Card key={coupon.id} sx={{ mb: 2, boxShadow: 3 }}>
                  <CardContent>
                    <Typography variant="h6" color="primary">
                      {coupon.code}
                    </Typography>
                    <Typography variant="body2" color="textSecondary">
                      Description: <strong>{coupon.description}</strong>
                    </Typography>
                    <Typography variant="body2" color="textSecondary">
                      Assigned to: <strong>{coupon.assignedTo}</strong>
                    </Typography>
                  </CardContent>
                </Card>
              ))}
            </List>
          ) : (
            <Typography textAlign="center" color="textSecondary">
              No assigned coupons available.
            </Typography>
          )}
        </DialogContent>
        <DialogActions sx={{ justifyContent: 'center', backgroundColor: '#f3f4f6' }}>
          <Button onClick={() => setOpenAssignedCoupons(false)} variant="contained" color="error">
            Close
          </Button>
        </DialogActions>
      </Dialog>
    </>
  );
};

export default Navbar;
